// ===================================
// LOGICA APLICAȚIEI
// ===================================

let currentTest = [];
let currentQuestionIndex = 0;
let userAnswers = [];
let score = 0;
let answered = false;
let stats = {
    testsDone: 0,
    totalScore: 0
};

// Încarcă statistici din localStorage
function loadStats() {
    const saved = localStorage.getItem('quizStats');
    if (saved) {
        stats = JSON.parse(saved);
        updateStatsDisplay();
    }
}

// Salvează statistici în localStorage
function saveStats() {
    localStorage.setItem('quizStats', JSON.stringify(stats));
}

// Actualizează afișarea statisticilor
function updateStatsDisplay() {
    document.getElementById('testsDone').textContent = stats.testsDone;
    const avgScore = stats.testsDone > 0 
        ? Math.round((stats.totalScore / stats.testsDone) * 100) 
        : 0;
    document.getElementById('avgScore').textContent = avgScore + '%';
}

// Generează test nou cu 18 întrebări random
function generateRandomTest() {
    const shuffled = [...allQuestions].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, 18);
}

// Start test nou
function startNewTest() {
    currentTest = generateRandomTest();
    currentQuestionIndex = 0;
    score = 0;
    
    showScreen('testScreen');
    renderQuestion();
}

// Afișează întrebarea curentă
function renderQuestion() {
    const question = currentTest[currentQuestionIndex];
    userAnswers = [];
    answered = false;
    
    // Update progress
    const progress = ((currentQuestionIndex + 1) / currentTest.length) * 100;
    document.getElementById('progressBar').style.width = progress + '%';
    document.getElementById('currentQuestion').textContent = currentQuestionIndex + 1;
    document.getElementById('totalQuestions').textContent = currentTest.length;
    
    // Render question
    document.getElementById('questionNum').textContent = currentQuestionIndex + 1;
    document.getElementById('questionText').textContent = question.q;
    
    // Render answers
    let answersHTML = '';
    for (let key in question.a) {
        answersHTML += `
            <div class="answer-option" onclick="toggleAnswer('${key}')">
                <input type="checkbox" id="ans_${key}" value="${key}">
                <div class="answer-text">${key}) ${question.a[key]}</div>
            </div>
        `;
    }
    document.getElementById('answersContainer').innerHTML = answersHTML;
    
    // Reset button
    document.getElementById('actionBtn').textContent = 'Verifică răspunsul';
}

// Toggle răspuns
function toggleAnswer(key) {
    if (answered) return;
    
    const checkbox = document.getElementById('ans_' + key);
    checkbox.checked = !checkbox.checked;
    
    if (checkbox.checked) {
        if (!userAnswers.includes(key)) {
            userAnswers.push(key);
        }
    } else {
        userAnswers = userAnswers.filter(ans => ans !== key);
    }
}

// Handle button action (verificare sau următoarea)
function handleAction() {
    if (!answered) {
        checkAnswer();
    } else {
        nextQuestion();
    }
}

// Verifică răspunsul
function checkAnswer() {
    if (userAnswers.length === 0) {
        alert('Te rog selectează cel puțin un răspuns!');
        return;
    }
    
    answered = true;
    const question = currentTest[currentQuestionIndex];
    
    const correctSorted = question.correct.slice().sort().join(',');
    const userSorted = userAnswers.slice().sort().join(',');
    
    const isCorrect = correctSorted === userSorted;
    if (isCorrect) score++;
    
    // Marchează răspunsurile
    const options = document.querySelectorAll('.answer-option');
    options.forEach(option => {
        const checkbox = option.querySelector('input');
        checkbox.disabled = true;
        option.classList.add('disabled');
        
        if (question.correct.includes(checkbox.value)) {
            option.classList.add('correct');
        } else if (checkbox.checked) {
            option.classList.add('wrong');
        }
    });
    
    // Update button
    if (currentQuestionIndex === currentTest.length - 1) {
        document.getElementById('actionBtn').textContent = 'Vezi rezultatele';
    } else {
        document.getElementById('actionBtn').textContent = 'Următoarea întrebare';
    }
}

// Următoarea întrebare
function nextQuestion() {
    currentQuestionIndex++;
    
    if (currentQuestionIndex < currentTest.length) {
        renderQuestion();
    } else {
        showResults();
    }
}

// Afișează rezultatele
function showResults() {
    // Update stats
    stats.testsDone++;
    stats.totalScore += score;
    saveStats();
    updateStatsDisplay();
    
    // Calculate percentage
    const percentage = Math.round((score / currentTest.length) * 100);
    
    // Display results
    document.getElementById('finalScore').textContent = score;
    document.getElementById('scorePercentage').textContent = percentage + '%';
    document.getElementById('correctAnswers').textContent = score;
    document.getElementById('wrongAnswers').textContent = currentTest.length - score;
    
    // Message based on score
    const messageEl = document.getElementById('scoreMessage');
    if (percentage >= 90) {
        messageEl.textContent = '🎉 Excelent! Cunoști foarte bine materia!';
        messageEl.className = 'score-message excellent';
    } else if (percentage >= 70) {
        messageEl.textContent = '👍 Foarte bine! Continuă să înveți!';
        messageEl.className = 'score-message good';
    } else if (percentage >= 50) {
        messageEl.textContent = '📚 Bine, dar mai ai de lucrat!';
        messageEl.className = 'score-message average';
    } else {
        messageEl.textContent = '💪 Nu te descuraja! Mai încearcă!';
        messageEl.className = 'score-message poor';
    }
    
    showScreen('resultsScreen');
}

// Schimbă screen-ul afișat
function showScreen(screenId) {
    document.querySelectorAll('.screen').forEach(screen => {
        screen.classList.remove('active');
    });
    document.getElementById(screenId).classList.add('active');
}

// Înapoi la meniu
function goToMenu() {
    showScreen('mainMenu');
}

// Initialize app
loadStats();

